// Books.js

import React, { useState, useEffect } from "react";
import axios from "axios";
import UpdateBook from "./UpdateBook"; // Import the UpdateBook component
import './Books.css';

const AllBooks = () => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchType, setSearchType] = useState("title");
  const [selectedBook, setSelectedBook] = useState(null);

  const handleUpdate = (bookId) => {
    const bookToUpdate = books.find((book) => book.bookId === bookId);
    setSelectedBook(bookToUpdate);
  };

  const handleDelete = async (bookId) => {
    try {
      await axios.delete(`https://localhost:7034/api/Book/${bookId}`);
      setBooks((prevBooks) => prevBooks.filter((book) => book.bookId !== bookId));
    } catch (error) {
      console.error("Error deleting book:", error);
    }
  };

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        let response;

        if (searchTerm) {
          if (searchType === "author") {
            response = await axios.get(`https://localhost:7034/api/Book/author/${searchTerm}`);
          } else if (searchType === "title") {
            response = await axios.get(`https://localhost:7034/api/Book/title/${searchTerm}`);
          } else if (searchType === "genre") {
            response = await axios.get(`https://localhost:7034/api/Book/genre/${searchTerm}`);
          }
        } else {
          response = await axios.get("https://localhost:7034/api/Book");
        }

        setBooks(response.data);
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    };

    fetchBooks();
  }, [searchTerm, searchType]);

  return (
    <div className="booksContainer">
      <h2>All Books</h2>

      <div className="searchContainer">
        <input
          type="text"
          placeholder="Search term"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <select
          value={searchType}
          onChange={(e) => setSearchType(e.target.value)}
        >
          <option value="title">Title</option>
          <option value="author">Author</option>
          <option value="genre">Genre</option>
        </select>
      </div>

      <div className="booksGrid">
        {books.map((book) => (
          <div className="bookCard" key={book.bookId}>
            <p>Title: {book.title}</p>
            <p>Author: {book.author}</p>
            <p>Genre: {book.genre}</p>
            <p>ISBN: {book.isbn}</p>
            <p>Publish Date: {new Date(book.publishDate).toLocaleDateString()}</p>
            <p>Username: {book.username}</p>

            {/* Update and Delete buttons */}
            <button onClick={() => handleUpdate(book.bookId)}>Update</button>
            <button onClick={() => handleDelete(book.bookId)}>Delete</button>
          </div>
        ))}
      </div>

      {/* Render the UpdateBook component when a book is selected for update */}
      {selectedBook && (
        <UpdateBook
          bookId={selectedBook.bookId}
          title={selectedBook.title}
          author={selectedBook.author}
          genre={selectedBook.genre}
          isbn={selectedBook.isbn}
          publishDate={selectedBook.publishDate}
          username={selectedBook.username}
        />
      )}
    </div>
  );
};

export default AllBooks;
