import React from "react";
import { Link } from "react-router-dom";
import './Home.css'; 

const Home = () => {
  return (
    <div className="homeContainer">
      <div className="heroSection">
        <h1>Welcome to Our Online Book Store</h1>
        <p>Discover a world of knowledge with our vast collection of books.</p>
        <Link to="/books" className="exploreButton">
          Explore Books
        </Link>
      </div>

      <div className="featuredBooksSection">
        <h2>Featured Books</h2>
        {/* Display featured books with images and brief descriptions */}
        <div className="bookCard">
          <img src="book1.jpg" alt="Book Cover" />
          <h3>Book Title</h3>
          <p>Author Name</p>
          <Link to="/book-details/1" className="detailsButton">
            View Details
          </Link>
        </div>

      
      </div>

    </div>
  );
};

export default Home;
