import React, { useState } from 'react';

const AddBook = () => {
  // State to hold the form data
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    genre: '',
    isbn: '',
    publishDate: '',
    username: '',
  });

  // Function to handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // You can add further validation or API calls here to save the book data
    console.log('Book data submitted:', formData);
    // Reset the form after submission
    setFormData({
      title: '',
      author: '',
      genre: '',
      isbn: '',
      publishDate: '',
      username: '',
    });
  };

  return (
    <div>
      <h2>Add Book</h2>
      <form onSubmit={handleSubmit}>
        <label>Title:</label>
        <input type="text" name="title" value={formData.title} onChange={handleInputChange} required />

        <label>Author:</label>
        <input type="text" name="author" value={formData.author} onChange={handleInputChange} required />

        <label>Genre:</label>
        <input type="text" name="genre" value={formData.genre} onChange={handleInputChange} required />

        <label>ISBN:</label>
        <input type="text" name="isbn" value={formData.isbn} onChange={handleInputChange} required />

        <label>Publish Date:</label>
        <input type="date" name="publishDate" value={formData.publishDate} onChange={handleInputChange} required />

        <label>Username:</label>
        <input type="text" name="username" value={formData.username} onChange={handleInputChange} required />

        <button type="submit">Add Book</button>
      </form>
    </div>
  );
};

export default AddBook;
