import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UpdateDelete from './UpdateDelete';
import './AllBooks.css';

const AllBooks = () => {
    const [books, setBooks] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [searchType, setSearchType] = useState("title"); 
  
    useEffect(() => {
        const fetchBooks = async () => {
          try {
            let response;
            if (searchTerm) {
              // If there is a search term, use the appropriate API endpoint based on the search type
              if (searchType === "author") {
                response = await axios.get(`https://localhost:7034/api/Book/author/${searchTerm}`);
              } else if (searchType === "title") {
                response = await axios.get(`https://localhost:7034/api/Book/title/${searchTerm}`);
              } else if (searchType === "genre") {
                response = await axios.get(`https://localhost:7034/api/Book/genre/${searchTerm}`);
              }
            } else {
              // If no search term, fetch all books
              response = await axios.get("https://localhost:7034/api/Book");
            }
    
            setBooks(response.data);
          } catch (error) {
            console.error("Error fetching books:", error);
          }
        };
      fetchBooks();
    }, []);
  
    useEffect(() => {
      const results = books.filter(book =>
        book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        book.author.toLowerCase().includes(searchTerm.toLowerCase()) ||
        book.genre.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setSearchResults(results);
    }, [searchTerm, books]);
  
    const handleSearchChange = event => {
      setSearchTerm(event.target.value);
    };
  
    return (
      <div className="books-container">
        <h2>All Books</h2>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search by title, author, or genre"
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>
        <div className="books-list">
          {searchResults.map((book) => (
            <div key={book.bookId} className="book-card">
              <h3>{book.title}</h3>
              <p><strong>Author:</strong> {book.author}</p>
              <p><strong>Genre:</strong> {book.genre}</p>
              <p><strong>ISBN:</strong> {book.isbn}</p>
              <p><strong>Publish Date:</strong> {new Date(book.publishDate).toLocaleDateString()}</p>
              <UpdateDelete bookId={book.bookId} /> 
            </div>
          ))}
        </div>
      </div>
    );
};

export default AllBooks;
