import React, { useState, useEffect } from "react";
import axios from "axios";
import BookCard from "./BookCard"; // Import your BookCard component
import './Books.css'; // Import your CSS file for styling

const Books = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    // Fetch book data from your API or data source
    const fetchBooks = async () => {
      try {
        const response = await axios.get("https://localhost:7034/api/Book"); // Replace with your API endpoint
        setBooks(response.data);
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    };

    fetchBooks();
  }, []);

  return (
    <div className="booksContainer">
      <h2>All Books</h2>
      <div className="booksGrid">
        {books.map(book => (
          <BookCard key={book.bookId} book={book} />
        ))}
      </div>
    </div>
  );
};

export default Books;
