// UpdateBook.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UpdateBook.css';

const UpdateBook = ({ bookId, title: initialTitle, author: initialAuthor, genre: initialGenre, isbn: initialIsbn, publishDate: initialPublishDate, username: initialUsername, onUpdate }) => {
  const [title, setTitle] = useState(initialTitle);
  const [author, setAuthor] = useState(initialAuthor);
  const [genre, setGenre] = useState(initialGenre);
  const [isbn, setIsbn] = useState(initialIsbn);
  const [publishDate, setPublishDate] = useState(initialPublishDate);
  const [username, setUsername] = useState(initialUsername);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // You can perform additional actions on component mount or update
    // For example, fetching additional data based on bookId
    // Make sure to clean up any side effects if necessary
    return () => {
      // Cleanup logic here
    };
  }, [bookId]);

  const handleUpdate = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);
      setError(null);

      // Log the details for debugging
      console.log('Updating book with ID:', bookId);
      console.log('Updated book details:', { title, author, genre, isbn, publishDate, username });

      // Make the API call using Axios
      const response = await axios.put(`https://localhost:7034/api/Book/${bookId}`, {
        title,
        author,
        genre,
        isbn,
        publishDate,
        username,
      });

      // Handle the response accordingly (e.g., show success message)
      console.log('Book updated successfully:', response.data);

      // Optionally, notify the parent component about the update
      onUpdate && onUpdate(response.data);
    } catch (error) {
      // Handle errors (e.g., show error message)
      console.error('Error updating book:', error.message);
      setError('Failed to update book. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Update Book</h2>
      <form onSubmit={handleUpdate}>
        {/* Form fields */}
        {/* ... */}

        <button type="submit" disabled={loading}>Update Book</button>

        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
    </div>
  );
};

export default UpdateBook;
